// Given a string s, return the longest
// palindromic

// substring
//  in s.

// Example 1:

// Input: s = "babad"
// Output: "bab"
// Explanation: "aba" is also a valid answer.
// Example 2:

// Input: s = "cbbd"
// Output: "bb"

// Constraints:

// 1 <= s.length <= 1000
// s consist of only digits and English letters.

/**
 * @param {string} s
 * @return {string}
 */
var longestPalindrome = function (s) {
  const isPalindrome = (str) => {
    return str == str.split("").reverse().join("");
  };
  let len = s.length;
  let long_palin = "";
  for (let i = 0; i < len; i++) {
    for (let j = len - 1; j >= 0; j--) {
      let slice = s.slice(i, j + 1);
      if (isPalindrome(slice) && slice.length >= long_palin.length) {
        long_palin = slice;
      }
    }
  }

  return long_palin;
};

let testcase1 = "babad";
let testcase2 = "b";
let testcase3 =
  "";

console.log(longestPalindrome(testcase1));
console.log(longestPalindrome(testcase2));
console.log(longestPalindrome(testcase3));
